};{
[h: roll = 0]
[h: rolls =""]
[h: value = 0]
[h: botched=0]
}]

[h, if((type=="stress" || type =="quality")&& roll == 1),code:{
  [h, while( roll == 1 ), code: {
    [h: roll=1d10]
    [h: rolls=listAppend(rolls, roll)]		
  }]
  [h: value = roll * power(2,listCount(rolls)-1)]
}]

[h: botched = 0]
[h, if(type!="no" && roll == 0),code:{
  [h, count( json.get(jr, "botchDie")), code: {
    [h: roll=1d10]
    [h, if( roll == 10): roll=0]
    [h: rolls=listAppend(rolls, roll)]		
    [h, if(roll==0): botched=botched+1]
  }]
